INSERT INTO %dbprefix%receipt_template (template_id,template,is_default,template_name) VALUES
(1, '<h1 style="text-align:center;">[clinic_name]</h1><h2 style="text-align:center;">[tag_line]</h2><p style="text-align:center;">[clinic_address]</p><span class="contact"><p style="text-align: center;"><b style="line-height: 1.42857143;">Landline : </b><span style="line-height: 1.42857143;">[landline]  </span><b style="line-height: 1.42857143;">Mobile : </b><span style="line-height: 1.42857143;">[mobile]  </span><b style="line-height: 1.42857143;">Email : </b><span style="text-align: center;"> [email]</span></p></span><hr id="null"><h3 style="text-align: center;"><u style="text-align: center;">RECEIPT</u></h3><span style="text-align: left;"><b>Date : </b>[bill_date]</span><span style="float: right;"><b>Receipt Number :</b> [bill_id]</span><p style="text-align: left;"><b style="text-align: left;">Patient Name: </b><span style="text-align: left;">[patient_name]<br></span></p><hr id="null" style="text-align: left;">Received fees for Professional services and other charges of our:<p><br></p><table style="width: 100%;margin-top: 25px;margin-bottom: 25px;border-collapse: collapse;border:1px;"><thead><tr><td style="width: 400px;text-align: left;"><b style="width: 400px;text-align: left;">Item</b></td><td><b>Quantity</b></td><td style="width: 100px;text-align:right;"><b>M.R.P.</b></td><td style="width: 100px;text-align:right;"><b>Amount</b></td></tr></thead><tbody>[col:particular|quantity|mrp|amount]<tr><td colspan="3">Previous Due</td><td style="text-align:right;"><strong>[previous_due]</strong></td></tr><tr><td colspan="3">Total</td><td style="text-align:right;"><strong>[total]</strong></td></tr><tr><td colspan="3">Paid Amount</td><td style="text-align:right;">[paid_amount]</td></tr></tbody></table>Received with Thanks,<p>For [clinic_name]</p><p><br></p><p><br></p><p>Signature</p>', 1, 'Main');
