DELETE t1 FROM %dbprefix%data t1 INNER JOIN %dbprefix%data t2 WHERE t1.ck_data_id < t2.ck_data_id AND t1.ck_key = t2.ck_key;
ALTER TABLE %dbprefix%data ADD UNIQUE(ck_key);
CREATE TABLE %dbprefix%language_master (language_id int(11) NOT NULL AUTO_INCREMENT,language_name varchar(25) NOT NULL,is_rtl int(1) NOT NULL,PRIMARY KEY (language_id));
ALTER TABLE %dbprefix%language_master ADD UNIQUE(language_name); 
ALTER TABLE %dbprefix%language_master ADD is_default INT(1) NOT NULL DEFAULT '0' AFTER is_rtl; 
INSERT INTO %dbprefix%language_master (language_name) SELECT distinct l_name FROM %dbprefix%language_data;
INSERT INTO %dbprefix%navigation_menu (id, menu_name, parent_name, menu_order,menu_url, menu_icon, menu_text, required_module, is_deleted, sync_status) VALUES (NULL, 'language', 'administration', '', 'settings/language', NULL, 'language', NULL, NULL, NULL);
CREATE OR REPLACE VIEW %dbprefix%view_bill AS SELECT bill.bill_id AS bill_id,      bill.bill_date AS bill_date,      doctor.name AS doctor_name,      IFNULL(visit.doctor_id,bill.doctor_id) AS doctor_id,      patient.patient_id AS patient_id,      patient.display_id AS display_id,      contacts.first_name AS first_name,      contacts.middle_name AS middle_name,     contacts.last_name AS last_name,      bill.total_amount AS total_amount,      bill.tax_amount AS bill_tax_amount,    bill.due_amount AS due_amount,      SUM(IF(payment.payment_status != "rejected",bill_payment_r.adjust_amount,0)) AS pay_amount  FROM %dbprefix%bill AS bill      LEFT JOIN %dbprefix%patient patient ON bill.patient_id = patient.patient_id      LEFT JOIN %dbprefix%contacts contacts on contacts.contact_id = patient.contact_id      LEFT JOIN %dbprefix%visit visit ON bill.visit_id = visit.visit_id       LEFT JOIN %dbprefix%bill_payment_r bill_payment_r ON bill.bill_id = bill_payment_r.bill_id      LEFT JOIN %dbprefix%payment payment ON payment.payment_id = bill_payment_r.payment_id      LEFT JOIN %dbprefix%view_doctor doctor ON IFNULL(visit.doctor_id,bill.doctor_id) = doctor.doctor_id GROUP BY bill.bill_id ;
INSERT INTO %dbprefix%menu_access (id, menu_name, category_name, allow) VALUES (NULL, 'language', 'System Administrator', 1);
UPDATE %dbprefix%version SET current_version='0.9.1';
